import React, { useEffect } from "react";
import { useState } from "react";
import "../css/home.css";

const Home = () => {
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetch("https://restcountries.com/v3.1/name/fra")
      .then((response) => {
        if (!response.ok) {
          throw new Error(
            "Erreur lors de la requête, status :",
            response.status
          );
        }
        return response.json();
      })
      .then((data) => {
        console.log(data);
        setSearch(data);
      })
      .catch((error) => {
        console.error("Erreur fetch :", error);
      });
  }, []);

  return (
    <div className="flexContainer">
      <form className="form" action="">
        <input
          className="userInput"
          type="text"
          placeholder="Entrez un pays..."
        />
        <button className="buttonInput">Search</button>
      </form>
      <div className="responseContainer"></div>
    </div>
  );
};

export default Home;
